const moment = require('moment');
const constPhone=(number)=>{
 var phone = /^1[3-9]\d{9}$/
 if(phone.test(number)){
    return'1'
 }else{
    return'0'
 }
}
console.log(constPhone(18834283416),'手机号');
const code=()=>{
    return Math.random().toFixed(6).slice(-6);
}
const date = ()=>{
    return moment().format('yyyy-MM-DD hh:mm:ss');
}
console.log(date())
console.log(code())
module.exports={
constPhone,
code,
date
}